function loadSidebar(){const e=document.querySelector("sidebar");document.querySelectorAll("[toggle]").forEach(t=>{t.addEventListener("click",function(){e.hasAttribute("open")?e.removeAttribute("open"):e.setAttribute("open","")})}),document.querySelectorAll("[toggle-open]").forEach(t=>{t.addEventListener("click",()=>{e.hasAttribute("open")||(e.setAttribute("open",""),document.querySelectorAll("[toggle-open]").forEach(e=>e.setAttribute("disabled","")),document.querySelectorAll("[toggle-close]").forEach(e=>e.removeAttribute("disabled")))})}),document.querySelectorAll("[toggle-close]").forEach(t=>{t.addEventListener("click",()=>{e.hasAttribute("open")&&(e.removeAttribute("open"),document.querySelectorAll("[toggle-open]").forEach(e=>e.removeAttribute("disabled")),document.querySelectorAll("[toggle-close]").forEach(e=>e.setAttribute("disabled","")))})})}document.addEventListener("DOMContentLoaded",function(){loadSidebar()});